import { useState } from 'react';
import './App.css';
import CharacterCard from './components/CharacterCard';

function App() {
  const [selectedChars, setSelectedChars] = useState([]);

  return (
    <>
    <div className="title">
      <h1>Overwatch Character Selector</h1>
    </div>
    <div className="CharacterLabels">
      <CharacterCard selectedChars={selectedChars} setSelectedChars={setSelectedChars}/>
    </div>
    <div className='CharacterSelector'>
      <button className='SelectBTN'>SELECT</button>
      <label className='SelectLabelTest'>{selectedChars.join(', ')}</label>
    </div>
    </>
  );
}

export default App;
