import React, { useState } from 'react';

export default function DamageChars() {
    const [selectedChars, setSelectedChars] = useState([]);
    const damageChars = [
        'Ashe',
        'Bastion',
        'Cassidy',
        'Echo',
        'Genji',
        'Hanzo',
        'Junkrat',
        'Mei',
        'Pharah',
        'Reaper',
        'Sojourn',
        'Soldier: 76',
        'Sombra',
        'Symmetra',
        'Torbjörn',
        'Tracer',
        'Venture',
        'Widowmaker'
    ];

    const handleLabelClick = (char) => {
        if(!selectedChars.includes(char)){
            setSelectedChars([...selectedChars, char]);
        }
        console.log(selectedChars)
    }

    return (
        <>
        <h3>Damage Characters</h3>
        {damageChars.map((damage, index) => (
            <label key={index} onClick={handleLabelClick}>{damage}</label>
        ))}

        </>
    );
}
